@Component({
  selector: 'rich-demo',
  templateUrl: './rich-demo.html',
  styles: [`colorbox,.colorbox { display:inline-block; height:14px; width:14px;margin-right:4px; border:1px solid #000;}
    :host /deep/ .pull-right {
  float:right;
}

:host /deep/ .pull-left {
  float:left;
}

:host /deep/ .ui-select-toggle {
  height: auto;
  line-height: inherit;
}

:host /deep/ .ui-select-toggle hr {
  display: none;
}

:host /deep/ hr {
  margin: 0;
}`],
})
export class RichDemoComponent implements OnInit {
  private value:any = {};
  private _disabledV:string = '0';
  private disabled:boolean = false;
  private items:Array<any> = [];

  public ngOnInit():any {
    COLORS.forEach((it) => {
      this.items.push({
        id: it.factorCode,
        text: `<div><div style="color:#49b6d6;float:left">类别：</div>
          <div style="display:inline-block; width:8rem;float:left">${it.factorType}</div>
          <div style="color:#49b6d6;float:left">名称：</div>
          <div style="display:inline-block; width:16rem;text-overflow: ellipsis;white-space: nowrap;overflow: hidden;float:left" title="${it.factorName}">${it.factorName}</div>
          <div style="color:#49b6d6;float:left">代码：</div>
          <div style="float:left">${it.factorCode}</div><div style="clear:both;"></div></div>
          <div style="white-space: normal;"><span style="color:#49b6d6">描述公式：</span>${it.formulaCh}</div><hr>
          `
      });
    });
  }

  private get disabledV():string {
    return this._disabledV;
  }

  private set disabledV(value:string) {
    this._disabledV = value;
    this.disabled = this._disabledV === '1';
  }

  public selected(value:any):void {
    console.log('Selected value is: ', value);
  }

  public removed(value:any):void {
    console.log('Removed value is: ', value);
  }

  public typed(value:any):void {
    console.log('New search input: ', value);
  }

  public refreshValue(value:any):void {
    this.value = value;
  }
}
